mapper.select:
SELECT id, title, content, created_at, view_count, like_count, writer_nickname, profile_image
FROM boards
WHERE id = #{no}

mapper.update:
UPDATE boards
SET view_count = view_count + 1
WHERE id = #{no}

UPDATE boards
SET title = #{title}, content = #{content}
WHERE id = #{id}

UPDATE boards
SET like_count = like_count + 1;
WHERE id = #{no}
