mapper.select:
select id, email, nickname, profile_image, introduction, friend_code, oauth_provider, is_deleted
from members
where email = #{email}

select id, email, nickname, profile_image, introduction, friend_code, oauth_provider, is_deleted
from members
where id = #{id}

mapper.update:
update members
set nickname = #{nickname}, profile_image = #{profileImage}, introduction = #{introduction}
where id = #{id}
